INSERT INTO recipes (name, summary, description, portion) VALUES 
('Classic Caesar Salad', 'A traditional Caesar salad with crispy croutons and tangy dressing', 'This classic Caesar salad recipe includes crisp romaine lettuce, crunchy croutons, and a tangy dressing made with anchovies, garlic, and Parmesan cheese.', '2'),
